import { takeEvery } from 'redux-saga/effects';

import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';

import { fetchCollectionsSuccess, fetchCollectionsFailure } from '../shop/shop.actions';

import ShopActionsType from '../shop/shop.types';

export function* fetchCollectionsAsync() {
  yield console.log('Makan nasi sama indomie');

  const collectionRef = firestore.collection('collections');

  collectionRef.get().then(snapshot => {
    const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
    dispatch(fetchCollectionsSuccess(collectionsMap));
  }).catch(error => dispatch(fetchCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    ShopActionsType.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
